import {BrowserRouter, Route, Routes} from 'react-router-dom';
import './App.css';
import {Header} from "./Header";
import {Card} from "./Card";
import {List} from "./List";
import {Form} from "./Form";
import {useState} from "react";

const managerList = [
    {
        firstName: 'Дмитрий Р.И.',
    },
    {
        firstName: 'Алексей Р.И.',
    },
    {
        firstName: 'Анатолий Р.И.',
    },
    {
        firstName: 'Анатолий Р.И.',
    },
    {
        firstName: 'Геннадий Р.И.',
    }
];

const clientsList = [
    {
        managerId: 1,
        firstName: 'Дмитрий Р.И.',
        description: 'Предложение услуг'
    },
    {
        managerId: 3,
        firstName: 'Алексей Р.И.',
        description: 'Предложение работы'
    },
    {
        managerId: 4,
        firstName: 'Геннадий Р.И.',
        description: 'Предложение праздника'
    }
];

function App() {
    const [ managerListState, setManagerListState ] = useState(managerList);
    const [ clientsListState, setClientsListState ] = useState(clientsList);

    return (
        <div className="App">
            <BrowserRouter>
                <Header>Личный кабинет руководителя банковского отдела</Header>
                <div className="Content">
                    <Routes>
                        <Route path="/" element={
                            <>
                                <Card title="Список менеджеров">
                                    <List
                                        type={'manager'}
                                        arrayList={managerListState}
                                        setArrayList={setManagerListState}
                                        pathToAdd={"/add/manager"}
                                    />
                                </Card>
                                <Card title="Список клиентов">
                                    <List
                                        linkedArray={managerListState}
                                        arrayList={clientsListState}
                                        setArrayList={setClientsListState}
                                        pathToAdd={"/add/client"}
                                    />
                                </Card>
                            </>
                        }/>
                        <Route path="/add/manager" element={
                            <Card title="Добавление менеджера">
                                <Form
                                    list={managerListState}
                                    setList={setManagerListState}
                                />
                            </Card>
                        }/>
                        <Route path="/add/client" element={
                            <Card title="Добавление клиента">
                                <Form
                                    type={'clients'}
                                    action={''}
                                    list={clientsListState}
                                    setList={setClientsListState}
                                    options={managerListState}
                                />
                            </Card>
                        }/>
                        <Route path="/change/client" element={
                            <Card title="Добавление клиента">
                                <Form
                                    type={'clients'}
                                    action={'change'}
                                    list={clientsListState}
                                    setList={setClientsListState}
                                    options={managerListState}
                                />
                            </Card>
                        }/>
                    </Routes>
                </div>
            </BrowserRouter>
        </div>
    );
}

export default App;
