import {BrowserRouter, Route, Routes} from 'react-router-dom';
import './App.css';
import {Header} from "./Header";
import {Card} from "./Card";
import {List} from "./List";
import {AddForm} from "./AddForm";
import {useState} from "react";

const managerList = [
    {
        firstName: 'Дмитрий',
        lastName: 'Рогозин',
    },
    {
        firstName: 'Алексей',
        lastName: 'Мартынюк'
    },
    {
        firstName: 'Анатолий',
        lastName: 'Трудоношин'
    },
    {
        firstName: 'Анатолий',
        lastName: 'Щербаков'
    },
    {
        firstName: 'Геннадий',
        lastName: 'Сабуров'
    }
];

const clientsList = [
    {
        firstName: 'Дмитрий',
        lastName: 'Рогозин',
        description: 'Предложение услуг'
    },
    {
        firstName: 'Алексей',
        lastName: 'Мартынюк',
        description: 'Предложение работы'
    },
    {
        firstName: 'Геннадий',
        lastName: 'Сабуров',
        description: 'Предложение праздника'
    }
];

const options =  [
    'Антон',
    'Антонвыа',
    'Антоныва',
    'Антоныва',
];


function App() {
    const [ managerListState, setManagerListState ] = useState(managerList);

    return (
        <div className="App">
            <BrowserRouter>
                <Header>Личный кабинет руководителя банковского отдела</Header>
                <div className="Content">
                    <Routes>
                        <Route path="/" element={
                            <>
                                <Card title="Список менеджеров">
                                    <List
                                        arrayList={managerList}
                                        pathToAdd={"/add/manager"}
                                    />
                                </Card>
                                <Card title="Список клиентов">
                                    <List
                                        arrayList={clientsList}
                                        pathToAdd={"/add/client"}
                                    />
                                </Card>
                            </>
                        }/>
                        <Route path="/add/manager" element={
                            <Card title="Добавление менеджера">
                                <AddForm
                                    list={managerListState}
                                    setList={setManagerListState}
                                />
                            </Card>
                        }/>
                        <Route path="/add/client" element={
                            <Card title="Добавление клиента">
                                <List
                                    arrayList={clientsList}
                                    pathToAdd={"/"}
                                />
                            </Card>
                        }/>
                    </Routes>
                </div>
            </BrowserRouter>
        </div>
    );
}

export default App;
