import './index.css';
import {ListItem} from "../ListItem";
import {Button} from "../Button";
import {Link} from "react-router-dom";
import {useState} from "react";

export const List = (props) => {
    const { arrayList, pathToAdd, type, linkedArray } = props;
    const [ arrayListState, setArrayListState ] = useState(arrayList);

    return (
        <div>
            { arrayListState.map((value, index) => {
                let description = value.description;
                if (linkedArray) {
                    description = `${linkedArray[value.managerId].firstName} ${value.description}`;
                }
                return (
                    <ListItem
                        type={type}
                        key={index}
                        index={index}
                        list={arrayListState}
                        setState={setArrayListState}
                        title={`${value.firstName} · ${value.lastName}`}
                        description={description}
                    />);
            })}
            <Link to={pathToAdd} className="ArrayList-Add-Link">
                <Button className="ArrayList-Add" type="action">Добавить</Button>
            </Link>
        </div>
    );
}
