import './index.css';
import {Input} from "../Input";
import {useState} from "react";
import {Select} from "../Select";
import {Button} from "../Button";

export const AddForm = (props) => {
    const {list, setList, options, type} = props;
    const [name, setName] = useState('');
    const [state, setState] = useState('');
    const [reason, setReason] = useState('');

    const onChangeSelect = (event) => {
        setState(event.target.value);
    };

    const onChangeReason = (event) => {
        setReason(event.target.value);
    };

    const onChangeName = (event) => {
        setName(event.target.value);
    }

    const onSubmitAddForm = (event) => {
        event.preventDefault();
        if (!name || !state || !reason) {
            return;
        }
        const array = list.map((value) => {
            return value;
        });

        array.push({
            managerId: Number(state),
            firstName: name,
            description: reason,
        });

        setList(array);
    };

    return (
        <form onSubmit={onSubmitAddForm}>
            <Input
                title={"Имя"}
                onChange={onChangeName}
            />
            { type === 'clients' && (
                <Input
                    title={"Причина звонка"}
                    onChange={onChangeReason}
                />
            )}
            { type === 'clients' && (
                <Select
                    options={options.map((value) => {
                        return value.firstName;
                    })}
                    placeholder={"Выбери имя"}
                    onChange={onChangeSelect}
                />
            )}
            <Button submit={true} type="action">Добавить</Button>
        </form>
    );
};
