import './index.css';

export const Button = (props) => {
    const { type, icon } = props;

    if (icon) {
        return (
            <button className={`Button Button_type_${type}`}>
                { icon === 'edit' && <img src="./pencil-square.svg" alt=""/> }
                { icon === 'trash' && <img src="./trash.svg" alt=""/> }
            </button>
        );
    }

    return (
        <button className={`Button Button_type_${type}`}>{props.children}</button>
    );
}
