import './index.css';
import edit from './pencil-square.svg';
import trash from './trash.svg';

export const Button = (props) => {
    const { type, icon, className } = props;

    if (icon) {
        return (
            <button className={`Button Button_type_${type}`}>
                { icon === 'edit' && <img width="25px" src={edit} alt=""/> }
                { icon === 'trash' && <img width="25px" src={trash} alt=""/> }
            </button>
        );
    }

    return (
        <button className={`Button Button_type_${type} ${className}`}>{props.children}</button>
    );
}
