import './index.css';
import {ListItem} from "../ListItem";
import {Button} from "../Button";
import {Link} from "react-router-dom";
import {useState} from "react";

export const ManagerList = (props) => {
    const { managerList } = props;
    const [ managerListState, setManagerListState ] = useState(managerList);
    return (
        <div>
            { managerListState.map((value, index) => {
                return (
                    <ListItem
                        key={index}
                        index={index}
                        list={managerListState}
                        setState={setManagerListState}
                        title={`${value.firstName} ${value.lastName}`}
                    />);
            })}
            <Link to="/add/manager" className="ManagerList-Add-Link">
                <Button className="ManagerList-Add" type="action">Добавить</Button>
            </Link>
        </div>
    );
}
